public function getCategoryMobileImage($category_id) {
  $mobile_image = '';
  $query = $this->db->query("SELECT * FROM " . DB_PREFIX . "category_mobile WHERE category_id = '" . (int)$category_id . "'");
  foreach ($query->rows as $result) {
    $mobile_image = $result['image'];
  }
  return $mobile_image;
}

