				if (is_mobile()) {
					// Payment Methods
					$method_count = 0;
					$this->load->model('extension/extension');
					$results = $this->model_extension_extension->getExtensions('payment');
					$recurring = $this->cart->hasRecurringProducts();

					foreach ($results as $result) {
						if ($this->config->get($result['code'] . '_status')) {
							$method_count += 1;
						}
					}
